    name="primary_ip_test",
    datacenter="fsn1-dc14",
    type="ipv4",
    assignee_type="server",
    auto_delete=True,
    labels={
        "hallo": "welt",
    })
# Link a server to a primary IP
server_test = hcloud.Server("server_test",
    name="test-server",
    image="ubuntu-20.04",
    server_type="cx22",
    datacenter="fsn1-dc14",
    labels={
