    withSelector: "key=value",
});
// Link a server to an existing primary IP
const serverTest = new hcloud.Server("server_test", {
    name: "test-server",
    image: "ubuntu-20.04",
    serverType: "cx22",
    datacenter: "fsn1-dc14",
    labels: {
